$(document).ready(function(){$("#auditContents,#auditContentsLocAssg").on("shown.bs.collapse",function(n){$('a[href="#'+n.target.id+'"] span').removeClass("glyphicon-plus").addClass("glyphicon-minus");$("html, body").animate({scrollTop:$("#"+n.target.id).offset().top},2e3)}).on("hidden.bs.collapse",function(n){$('a[href="#'+n.target.id+'"] span').removeClass("glyphicon-minus").addClass("glyphicon-plus")}).one("shown.bs.collapse",function(){$("div.ajaxContent",this).load($(this).attr("data-url"),function(n,t,i){if(t=="error")alert("Sorry but there was an error: "+i.status+" "+i.statusText)})})});